import base64import randomimport stringdef encode(text):    enc_bytes = text.encode('ascii')    base64_bytes = base64.b64encode(enc_bytes)    base64_enc = base64_bytes.decode('ascii')    return base64_encdef decode(text):    base64_bytes = text.encode('ascii')    text_bytes = base64.b64decode(base64_bytes)    decoded_text = text_bytes.decode('ascii')    return decoded_textconfirmation_code = encode(            ''.join(random.choice(                string.printable            ) for _ in range(50))        )print()print(f'Ваш код подтверждения: {confirmation_code}')