from rest_framework.routers import DefaultRouterfrom api.titles.views import CategoryViewSet, GenreViewSet, TitlesViewSet, ReviewViewSet, CommentViewSetfrom api.users.views import UserDetailViewSetrouter = DefaultRouter()router.register(r'users', UserDetailViewSet)router.register(r'categories', CategoryViewSet)router.register(r'genres', GenreViewSet)router.register(r'titles', TitlesViewSet)router.register(r'titles/(?P<title_id>\d+)/reviews', ReviewViewSet, basename='reviews')router.register(r'titles/(?P<title_id>\d+)/reviews/(?P<review_id>\d+)/comments', CommentViewSet, basename='comments')