from django_filters import rest_framework as filtersfrom api.titles.models import Titles, Genre, Categoryclass TitleFilter(filters.FilterSet):    category = filters.ModelMultipleChoiceFilter(field_name='category__slug', to_field_name='slug',                                                 queryset=Category.objects.all())    genre = filters.ModelMultipleChoiceFilter(field_name='genre__slug', to_field_name='slug',                                              queryset=Genre.objects.all())    name = filters.CharFilter(field_name='name', lookup_expr='icontains')    year = filters.NumberFilter(field_name='year')    class Meta:        model = Titles        fields = ['category', 'genre', 'name', 'year']