from django.urls import path, includefrom rest_framework_simplejwt.views import TokenObtainPairView, TokenRefreshViewfrom api.router import routerfrom api.users.views import RegisterView, UserViewSeturlpatterns = [    path('token/', TokenObtainPairView.as_view(), name='token_obtain_pair'),    path('token/refresh/', TokenRefreshView.as_view(), name='token_refresh'),    path('auth/email/', RegisterView.as_view()),    path('users/me/', UserViewSet.as_view({        'get': 'get_me',        'patch': 'patch_me',        'put': 'patch_me',    })),    path('', include(router.urls)),]