from django.contrib.auth.base_user import BaseUserManagerclass UserManager(BaseUserManager):    def create_user(self, email, password=None, **kwargs):        if not email:            raise ValueError('Email должен быть задан')        user = self.model(            email=self.normalize_email(email),            **kwargs,        )        user.set_password(password)        user.save(using=self._db)        return user    def create_superuser(self, email, password=None, **extra_fields):        user = self.create_user(            email,            password=password,            **extra_fields,        )        user.role = 'admin'        user.is_active = True        user.is_admin = True        user.is_staff = True        user.is_superuser = True        user.save(using=self._db)        return user