from rest_framework import permissionsclass UserPermission(permissions.BasePermission):    def has_permission(self, request, view):        return request.user.is_authenticated and request.user.role == 'admin'class BasePermission(permissions.BasePermission):    def has_permission(self, request, view):        if request.method in permissions.SAFE_METHODS:            return True        return request.user.is_authenticated and request.user.role == 'admin'class ReviewPermission(permissions.BasePermission):    def has_permission(self, request, view):        if request.method in permissions.SAFE_METHODS:            return True        return request.user.is_authenticated    def has_object_permission(self, request, view, obj):        if request.method in permissions.SAFE_METHODS:            return True        elif request.method == 'POST':            return request.user.is_authenticated        elif request.method in ('PATCH', 'DELETE'):            return request.user.role in ('admin', 'moderator') or obj.author == request.user